function SceneController.EnterTestDungeonBattle(data, placeName, endCall, enterCall)
    BattleData.placeName = placeName
    BattleData.worldWarTest = false
    SM.AddScene("Battle", require("Module.Battle.BattleScene"), function()
        BattleData.scene.ui.addLvbuBtn:SetActive(false)
        BattleData.scene.ui.addZhaoyunBtn:SetActive(false)
        local cardListT = BattleData.scene.ui.cardListT
        for i = 0, cardListT.childCount-1 do
            cardListT:GetChild(i).gameObject:SetActive(cardListT:GetChild(i).name == "huaxiong")
        end
        if enterCall then
            enterCall()
        end
    end, true, nil, data or TEST_SHANZEI, endCall)
end

